import { Knex } from 'knex';

const DB_SCHEMA = process.env.DB_SCHEMA;
const DB_SCHEMA_DAPI_V1 = process.env.DB_SCHEMA_DAPI_V1;

/**
 * Add spatial transform
 *
 * @export
 * @param {Knex} knex
 * @return {*}  {Promise<void>}
 */
export async function seed(knex: Knex): Promise<void> {
  await knex.raw(`
    SET SCHEMA '${DB_SCHEMA}';
    SET SEARCH_PATH = ${DB_SCHEMA}, ${DB_SCHEMA_DAPI_V1};
  `);

  const response = await knex.raw(`
    ${checkTemplateExists()}
  `);

  if (!response?.rows?.[0]) {
    await knex.raw(`
      ${insertOccurrenceTemplate()}
    `);
  }
}

const validationName = 'Moose Aerial SRB Recruitment Composition Survey';
const validationVersion = '2.0';
const mooseWldtaxonomicUnitsId = 2065;

const checkTemplateExists = () => `
  SELECT
    template_id
  FROM
    template
  WHERE
    name = 'Moose Aerial SRB Recruitment Composition Survey';
`;

/**
 * SQL to insert Occurrence Template
 *
 */
const insertOccurrenceTemplate = () => `
  WITH new_template_record AS (
    INSERT INTO
        template (name, version, record_effective_date, description)
      VALUES
        ('${validationName}', '${validationVersion}', now(), '${validationName}')
      RETURNING template_id
  )
  INSERT INTO template_methodology_species
  (template_id, wldtaxonomic_units_id, validation, transform)
  VALUES (
    (SELECT template_id FROM new_template_record),
    ${mooseWldtaxonomicUnitsId},
    '${mooseOccurrenceValidation}',
    '${mooseOccurenceTransformation}'
  );

`;

